import React from 'react';
import { useState } from 'react'; // function import
import './ExpenseItem.css';
import Card from "../UI/Card";
import ExpenseDate from './ExpenseDate';
const ExpenseItem= (props)=> {

  // react hook (hook start from use)
  // import from React library
  // function return so this function return array in this array (variable + function)
 
 
  //array destructuring 
   
  const[newTitle, setNewTitle] = useState("Hi");
  const[title, setTitle] = useState(props.title);


  const clickHandler=()=>{

    // change with setTitle function

    setTitle(newTitle);
  }

  const changeHandler=(event)=>{
    setNewTitle(event.target.value)
    
  }

  // variable and object pass with the {}cury bracket  
  return (
    <Card className="expense-item">
      <ExpenseDate date = {props.date}/>
      <div className="expense-description">
        <h2>{title}</h2>
        <div className="expense-amount">{props.amount}</div>
      </div>
      <input type='text' value = {newTitle} onChange={changeHandler}/>
      <button onClick={clickHandler}>change title</button>
    </Card>
  );
}

export default ExpenseItem;
