import React, {useState} from 'react'; // useState is function which return array and function 
import './ExpenseItem.css';
import ExpenseDate from './ExpenseDate';

import Card from './Card';

function ExpenseItem(props) {
    // Check if props.date is defined before accessing its properties
    // const month = 1 // date is object
    // const year = 2024
    // const day =  1


    const [newTitle, setNewTitle] = useState("");

    const [title, setTitle] = useState(props.title); // array destructuring
          // own name variable and function

    

    const clickMe = () =>
    {
        setTitle(newTitle); // this function works is to change the variable 

    }

    
    const changeHandler = (event) =>
    {
        setNewTitle(event.target.value); // this function works is to change the variable 

    }
    return (
        <div className='expense-item'>

            <ExpenseDate date ={props.date} />
           
            <div className='expense-item_description'>
                {/* <h2>{props.title}</h2> */}
                 <h2>{title}</h2>
                <div className='expense-item_price'>${props.amount}</div>
            </div>
            <input type = "text" value = {newTitle} onChange = {changeHandler} />
            <button onClick={clickMe }>click me</button>
        </div>
    );
}

export default ExpenseItem;
